package second;

public class MethodsInMathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(Math.pow(2, 3));      //2 to the power of 3
		
		System.out.println(Math.ceil(4.3));      //rounds the number UP to the nearest integer
		System.out.println(Math.floor(5.8));     //rounds the number DOWN to the nearest integer
		
		System.out.println(Math.abs(-7.5));      //absolute value of the number
		
		System.out.println(Math.max(7.7, 6.6));  //finds the largest of the two
		System.out.println(Math.min(8.8, 7.7));  //finds the smallest of the two
		
		System.out.println(Math.sqrt(16));       //finds the square root of the number
	}

}
